//             <div>
//                 {this.props.city && this.props.country && <p>LOCATION : {this.props.city}, {this.props.country}</p> }
//                 {this.props.temperature && <p>TEMPERATURE : {this.props.temperature}</p> }
//                 {this.props.humidity && <p>HUMIDITY : {this.props.humidity}</p> }
//                 {this.props.description && <p>CONDITIONS : {this.props.description}</p> }
//                 {this.props.error && <p>{this.props.error}</p>}
//             </div>
//         );
//     }
// }


const Weather = props => (
    <div>
        {
            props.city && props.country && 
                <p className="weather__key location">
                    <span className="weather__value"> {props.city}, {props.country}</span>
                </p> 
        }
        {
            props.temperature && 
                <p className="weather__key temperature">
                    <span className="weather__value"> {props.temperature}°C</span>
                </p> 
        }
       {
            props.temperature && 
                <p className="weather__key min_max_temperature">
                    <span className="weather__value"> {props.min_temperature}°C | {props.max_temperature}°C</span>
                </p> 
        }
        {
            props.humidity && 
                <p className="weather__key">HUMIDITY : 
                    <span className="weather__value"> {props.humidity} %</span>
                </p> 
        }
        {
            props.description && 
                <p className="weather__key">CONDITIONS :     
                    <span className="weather__value"> {props.description}</span>
                </p> 
        }
        {
            props.error && <p className="weather__error">{props.error}</p>
        }
    </div>  
);


export default Weather;
