import React from "react";

const ic = this.weather_icon;

const Weather = (props) => (

    <div>
        {
            props.city && props.country && 
                <p className="weather__key location">
                    {props.city}, {props.country}
                </p> 
        }
        {/* http://openweathermap.org/img/w/${props.weather_icon}.png */}
        {
            props.temperature && 
                <p className="weather__key">                    
                    <img className="weather_icon" src={"http://openweathermap.org/img/w/$ic.png"} />
                </p> 
        }
        {
            props.temperature && 
                <p className="weather__key temperature">
                    {props.temperature}
                </p> 
        }
       {
            props.temperature && 
                <p className="weather__key min_max_temperature">
                    {props.min_temperature}°C | {props.max_temperature}°C
                </p> 
        }
        {
            props.humidity && 
                <p className="weather__key humidity">HUMIDITY : 
                    {props.humidity} %
                </p> 
        }
        {
            props.wind && 
                <p className="weather__key wind_speed">WIND SPEED : 
                    {props.wind} m/s
                </p> 
        }
        {
            props.description && 
                <p className="weather__key conditions">   
                    {props.description}
                </p> 
        }
        {
            props.error && <h3 className="weather__error error">{props.error}</h3>
        }
    </div>  
);


export default Weather;
